import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

interface MapProps {
  locations: {
    location1: [number, number];
    location2: [number, number];
    midpoint: [number, number];
  };
}

const Map: React.FC<MapProps> = ({ locations }) => {
  const bounds = L.latLngBounds([locations.location1, locations.location2]);
  const polyline = [locations.location1, locations.midpoint, locations.location2];

  return (
    <MapContainer 
      bounds={bounds}
      style={{ height: '400px', width: '100%' }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; OpenStreetMap contributors'
      />
      <Marker position={locations.location1}>
        <Popup>Location 1</Popup>
      </Marker>
      <Marker position={locations.location2}>
        <Popup>Location 2</Popup>
      </Marker>
      <Marker position={locations.midpoint}>
        <Popup>Midpoint</Popup>
      </Marker>
      <Polyline positions={polyline} color="blue" />
    </MapContainer>
  );
};

export default Map;
