import axios from 'axios';

interface GeocodeResult {
  lat: number;
  lon: number;
  formattedAddress: string;
}

// Google Geocoding API key - in a real app, store this securely
// In Expo, you'd typically use environment variables via app.config.js or .env with expo-constants
const GOOGLE_GEOCODING_API_KEY = 'YOUR_API_KEY'; // Replace with your actual key

/**
 * Convert an address string to geocoordinates (lat/lon)
 * @param address The address to geocode
 * @returns Promise with geocoding result
 */
export const geocodeAddress = async (address: string): Promise<GeocodeResult> => {
  try {
    const response = await axios.get(
      `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(address)}&key=${GOOGLE_GEOCODING_API_KEY}`
    );

    if (response.data.status !== 'OK') {
      throw new Error(`Geocoding failed with status: ${response.data.status}`);
    }

    if (!response.data.results || response.data.results.length === 0) {
      throw new Error('No geocoding results found');
    }

    const result = response.data.results[0];
    const location = result.geometry.location;

    return {
      lat: location.lat,
      lon: location.lng,
      formattedAddress: result.formatted_address
    };
  } catch (error) {
    console.error('Geocoding error:', error);
    throw new Error('Failed to geocode address');
  }
};
