import axios from 'axios';

export interface Review {
  rating: number;
  text: string;
  date: string;
}

export interface Place {
  id: string;
  name: string;
  type: string;
  latitude: number;
  longitude: number;
  address?: string;
  distanceToLoc1?: number;
  distanceToLoc2?: number;
  imageUrl?: string;
  reviews?: Review[];
  rating?: number;
}

const GOOGLE_PLACES_API_KEY = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;

export const searchNearbyPlaces = async (latitude: number, longitude: number, radius: number = 6400): Promise<Place[]> => {
  try {
    console.log('Searching for places near:', { latitude, longitude });
    
    // Use text search which has better CORS support
    const response = await axios.get(
      `https://maps.googleapis.com/maps/api/place/textsearch/json`,
      {
        params: {
          query: 'restaurants OR cafes OR bars OR entertainment',
          location: `${latitude},${longitude}`,
          radius: radius,
          key: GOOGLE_PLACES_API_KEY
        }
      }
    );

    if (!response.data?.results) {
      console.log('No places found in response');
      return [];
    }

    const places = response.data.results.map((place: any) => ({
      id: place.place_id,
      name: place.name,
      type: place.types?.[0] || 'venue',
      latitude: place.geometry.location.lat,
      longitude: place.geometry.location.lng,
      address: place.formatted_address,
      rating: place.rating,
      imageUrl: place.photos?.[0] ? 
        `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=${GOOGLE_PLACES_API_KEY}` :
        undefined
    }));

    return places;
  } catch (error) {
    console.error('Error fetching places:', error);
    throw new Error('Failed to fetch nearby places in London');
  }
};
