import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Home: React.FC = () => {
  const [location1, setLocation1] = useState('');
  const [location2, setLocation2] = useState('');
  const history = useHistory();

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!location1.trim() || !location2.trim()) {
      alert('Please enter both postcodes');
      return;
    }
    history.push(`/activities?loc1=${encodeURIComponent(location1.trim())}&loc2=${encodeURIComponent(location2.trim())}`);
  };

  return (
    <div className="home">
      <h1>Find Activities Halfway</h1>
      <p>Enter two London postcodes to find activities at your midpoint</p>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter first postcode (e.g., SW1A 1AA)"
          value={location1}
          onChange={(e) => setLocation1(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter second postcode (e.g., E1 6AN)"
          value={location2}
          onChange={(e) => setLocation2(e.target.value)}
        />
        <button type="submit">Find Activities</button>
      </form>
    </div>
  );
};

export default Home;
