import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Place, searchNearbyPlaces } from '../services/placesService';
import { geocodeAddress } from '../services/geocodingService';
import { calculateDistance } from '../utils/distance';
import Map from './Map';

const ActivityList: React.FC = () => {
  const [places, setPlaces] = useState<Place[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [locations, setLocations] = useState<{
    location1: [number, number];
    location2: [number, number];
    midpoint: [number, number];
  } | null>(null);

  const location = useLocation();

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        setLoading(true);
        setError(null);
        const params = new URLSearchParams(location.search);
        const loc1 = params.get('loc1');
        const loc2 = params.get('loc2');

        if (!loc1 || !loc2) {
          throw new Error('Both locations are required');
        }

        const [pos1, pos2] = await Promise.all([
          geocodeAddress(loc1),
          geocodeAddress(loc2)
        ]);

        const midLat = (pos1.lat + pos2.lat) / 2;
        const midLon = (pos1.lon + pos2.lon) / 2;

        const newLocations = {
          location1: [pos1.lat, pos1.lon] as [number, number],
          location2: [pos2.lat, pos2.lon] as [number, number],
          midpoint: [midLat, midLon] as [number, number]
        };
        setLocations(newLocations);

        // Use fixed 4-mile (6400m) radius
        const radius = 6400;

        const nearbyPlaces = await searchNearbyPlaces(midLat, midLon, radius);
        if (nearbyPlaces.length === 0) {
          setError('No places found within 4 miles of the midpoint. Try different locations.');
          return;
        }

        const placesWithDistances = nearbyPlaces.map(place => ({
          ...place,
          distanceToLoc1: calculateDistance(pos1.lat, pos1.lon, place.latitude, place.longitude),
          distanceToLoc2: calculateDistance(pos2.lat, pos2.lon, place.latitude, place.longitude)
        }));

        setPlaces(placesWithDistances);
        setError(null);
      } catch (err) {
        console.error('Error fetching places:', err);
        setError(err instanceof Error ? err.message : 'Failed to load places');
        setPlaces([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPlaces();
  }, [location]);

  return (
    <div>
      {locations && (
        <div style={{ marginBottom: '20px' }}>
          <Map locations={locations} />
        </div>
      )}
      <div className="activity-list">
        {loading && <div className="loading">Loading places...</div>}
        {error && <div className="error">{error}</div>}
        {!loading && !error && (
          <>
            <h2>Places Near Midpoint</h2>
            {places.map(place => (
              <div key={place.id} className="activity-item">
                <h3>{place.name}</h3>
                <p>{place.type}</p>
                {place.address && <p>{place.address}</p>}
                <p>
                  Distance: {place.distanceToLoc1?.toFixed(1)}km from location 1, {' '}
                  {place.distanceToLoc2?.toFixed(1)}km from location 2
                </p>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default ActivityList;
