import React, { useEffect, useState } from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  FlatList, 
  Image, 
  TouchableOpacity, 
  ActivityIndicator,
  Linking,
  Dimensions
} from 'react-native';
import { RouteProp } from '@react-navigation/native';
import { RootStackParamList } from '../App';
import MapView, { Marker } from 'react-native-maps';
import { Ionicons } from '@expo/vector-icons';
import * as Location from 'expo-location';

// Import services
import { searchNearbyPlaces } from '../services/placesService';
import { geocodeAddress } from '../services/geocodingService';
import { calculateDistance } from '../utils/distance';

type ActivityListScreenRouteProp = RouteProp<RootStackParamList, 'ActivityList'>;

interface Props {
  route: ActivityListScreenRouteProp;
}

interface Place {
  id: string;
  name: string;
  type: string;
  rating: number;
  address: string;
  imageUrl: string | null;
  googleMapsUrl: string;
  latitude: number;
  longitude: number;
  distanceToMid?: number;
  distanceToLoc1?: number;
  distanceToLoc2?: number;
  reviews?: Review[];
}

interface Review {
  author: string;
  rating: number;
  text: string;
  date: number;
}

interface LocationCoords {
  location1: [number, number];
  location2: [number, number];
  midpoint: [number, number];
}

const ActivityListScreen: React.FC<Props> = ({ route }) => {
  const { loc1, loc2 } = route.params;
  const [places, setPlaces] = useState<Place[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [locations, setLocations] = useState<LocationCoords | null>(null);
  const [expandedReviews, setExpandedReviews] = useState<{ [key: string]: boolean }>({});

  const toggleReviews = (placeId: string) => {
    setExpandedReviews(prev => ({
      ...prev,
      [placeId]: !prev[placeId]
    }));
  };

  const truncateText = (text: string, maxLength: number = 100): string => {
    if (!text) return '';
    if (text.length <= maxLength) return text;
    return text.substring(0, text.lastIndexOf(' ', maxLength)) + '...';
  };

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        setLoading(true);
        setError(null);

        // Check for location permissions
        const { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
          setError('Permission to access location was denied');
          setLoading(false);
          return;
        }

        console.log('Fetching coordinates for:', { loc1, loc2 });

        const [pos1, pos2] = await Promise.all([
          geocodeAddress(loc1),
          geocodeAddress(loc2)
        ]).catch(error => {
          console.error('Geocoding error:', error);
          throw new Error('Could not find one or both locations. Please try more specific addresses.');
        });

        const midLat = (pos1.lat + pos2.lat) / 2;
        const midLon = (pos1.lon + pos2.lon) / 2;

        console.log('Calculated midpoint:', { midLat, midLon });

        const newLocations = {
          location1: [pos1.lat, pos1.lon] as [number, number],
          location2: [pos2.lat, pos2.lon] as [number, number],
          midpoint: [midLat, midLon] as [number, number]
        };
        setLocations(newLocations);

        // Search for various establishment types at the midpoint
        const radius = 500; // 500 meters radius
        const nearbyPlaces = await searchNearbyPlaces(midLat, midLon, radius, 'restaurant|bar|cafe|pub');
        console.log('Found nearby places:', nearbyPlaces);
        
        if (!nearbyPlaces.length) {
          throw new Error('No places found. Try different locations or a larger search area.');
        }

        const placesWithDistances = nearbyPlaces
          .map((place: any) => ({
            ...place,
            distanceToMid: calculateDistance(midLat, midLon, place.latitude, place.longitude),
            distanceToLoc1: calculateDistance(pos1.lat, pos1.lon, place.latitude, place.longitude),
            distanceToLoc2: calculateDistance(pos2.lat, pos2.lon, place.latitude, place.longitude)
          }))
          .sort((a: any, b: any) => a.distanceToMid - b.distanceToMid);

        setPlaces(placesWithDistances);
        setError(null);
      } catch (err) {
        console.error('Error in ActivityListScreen:', err);
        setError(err instanceof Error ? err.message : 'Failed to load places');
        setPlaces([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPlaces();
  }, [loc1, loc2]);

  const openInMaps = (place: Place) => {
    Linking.openURL(place.googleMapsUrl).catch(err => {
      console.error('Error opening map link:', err);
      setError('Could not open map');
    });
  };

  const renderPlace = ({ item }: { item: Place }) => (
    <View style={styles.placeCard}>
      {item.imageUrl ? (
        <TouchableOpacity onPress={() => openInMaps(item)}>
          <Image
            source={{ uri: item.imageUrl }}
            style={styles.placeImage}
          />
          <View style={styles.imageOverlay}>
            <Text style={styles.viewInMapsText}>View in Maps</Text>
          </View>
        </TouchableOpacity>
      ) : (
        <View style={styles.noImagePlaceholder}>
          <Ionicons name="location" size={40} color="#ccc" />
        </View>
      )}
      
      <View style={styles.placeDetails}>
        <View style={styles.placeHeader}>
          <Text style={styles.placeName}>{item.name}</Text>
          <Text style={styles.placeType}>{item.type}</Text>
        </View>
        
        {item.rating && (
          <View style={styles.ratingContainer}>
            <Text style={styles.ratingText}>{item.rating.toFixed(1)} ⭐</Text>
          </View>
        )}
        
        {item.address && (
          <Text style={styles.address}>{item.address}</Text>
        )}
        
        <View style={styles.distanceInfo}>
          <Text style={styles.distanceText}>📍 {item.distanceToLoc1?.toFixed(1)}km from location 1</Text>
          <Text style={styles.distanceText}>📍 {item.distanceToLoc2?.toFixed(1)}km from location 2</Text>
        </View>
        
        {item.reviews && item.reviews.length > 0 && (
          <View style={styles.reviewsContainer}>
            <Text style={styles.reviewsHeader}>Top Reviews</Text>
            
            <View style={styles.reviewItem}>
              <View style={styles.reviewHeader}>
                <Text style={styles.reviewerName}>{item.reviews[0].author || "Anonymous"}</Text>
                <Text style={styles.reviewRating}>{item.reviews[0].rating} ⭐</Text>
              </View>
              <Text style={styles.reviewText}>
                {expandedReviews[item.id] 
                  ? item.reviews[0].text 
                  : truncateText(item.reviews[0].text)}
              </Text>
              <Text style={styles.reviewDate}>
                {item.reviews[0].date ? new Date(Number(item.reviews[0].date) * 1000).toLocaleDateString() : ''}
              </Text>
            </View>
            
            <TouchableOpacity 
              style={styles.showMoreButton}
              onPress={() => toggleReviews(item.id)}
            >
              <Text style={styles.showMoreButtonText}>
                {expandedReviews[item.id] ? 'Show Less' : 'Show More Reviews'}
              </Text>
            </TouchableOpacity>
            
            {expandedReviews[item.id] && item.reviews.slice(1).map((review, index) => (
              <View key={index} style={styles.reviewItem}>
                <View style={styles.reviewHeader}>
                  <Text style={styles.reviewerName}>{review.author || "Anonymous"}</Text>
                  <Text style={styles.reviewRating}>{review.rating} ⭐</Text>
                </View>
                <Text style={styles.reviewText}>{review.text}</Text>
                <Text style={styles.reviewDate}>
                  {review.date ? new Date(Number(review.date) * 1000).toLocaleDateString() : ''}
                </Text>
              </View>
            ))}
          </View>
        )}
      </View>
    </View>
  );

  return (
    <View style={styles.container}>
      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#4285F4" />
          <Text style={styles.loadingText}>Finding the best places for you...</Text>
        </View>
      ) : error ? (
        <View style={styles.errorContainer}>
          <Ionicons name="alert-circle" size={50} color="#f44336" />
          <Text style={styles.errorText}>{error}</Text>
        </View>
      ) : (
        <View style={styles.contentContainer}>
          {locations && (
            <View style={styles.mapContainer}>
              <MapView
                style={styles.map}
                initialRegion={{
                  latitude: locations.midpoint[0],
                  longitude: locations.midpoint[1],
                  latitudeDelta: 0.04,
                  longitudeDelta: 0.04,
                }}
              >
                <Marker
                  coordinate={{
                    latitude: locations.location1[0],
                    longitude: locations.location1[1],
                  }}
                  title="Location 1"
                  pinColor="#4285F4"
                />
                <Marker
                  coordinate={{
                    latitude: locations.location2[0],
                    longitude: locations.location2[1],
                  }}
                  title="Location 2"
                  pinColor="#4285F4"
                />
                <Marker
                  coordinate={{
                    latitude: locations.midpoint[0],
                    longitude: locations.midpoint[1],
                  }}
                  title="Midpoint"
                  pinColor="#DB4437"
                />
                {places.map((place) => (
                  <Marker
                    key={place.id}
                    coordinate={{
                      latitude: place.latitude,
                      longitude: place.longitude,
                    }}
                    title={place.name}
                    description={place.type}
                    pinColor="#0F9D58"
                  />
                ))}
              </MapView>
            </View>
          )}
          
          <FlatList
            data={places}
            renderItem={renderPlace}
            keyExtractor={(item) => item.id}
            contentContainerStyle={styles.listContainer}
            showsVerticalScrollIndicator={false}
          />
        </View>
      )}
    </View>
  );
};

