import axios from 'axios';

export interface Review {
  rating: number;
  text: string;
  date: string;
}

export interface Place {
  id: string;
  name: string;
  type: string;
  latitude: number;
  longitude: number;
  address?: string;
  distanceToLoc1?: number;
  distanceToLoc2?: number;
  imageUrl?: string;
  reviews?: Review[];
  rating?: number;
}

const GOOGLE_PLACES_API_KEY = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;

export const searchNearbyPlaces = async (latitude: number, longitude: number, radius: number = 6400): Promise<Place[]> => {
  try {
    console.log('Searching for places near:', { latitude, longitude });
    // London bounding box constraints
    const londonBounds = {
      south: 51.2867,
      north: 51.7225,
      west: -0.5339,
      east: 0.3177
    };

    // Ensure we're within London bounds
    if (latitude < londonBounds.south || latitude > londonBounds.north ||
        longitude < londonBounds.west || longitude > londonBounds.east) {
      console.warn('Location outside London bounds');
      throw new Error('Location must be within London');
    }

    const response = await axios.get(
      `https://overpass-api.de/api/interpreter`,
      {
        params: {
          data: `
            [out:json][timeout:25];
            (
              node[~"^(amenity|leisure)$"~"^(restaurant|cafe|bar|pub|park|cinema|theatre)$"](around:${radius},${latitude},${longitude});
              way[~"^(amenity|leisure)$"~"^(restaurant|cafe|bar|pub|park|cinema|theatre)$"](around:${radius},${latitude},${longitude});
            );
            out body;
            >;
            out skel qt;
          `
        }
      }
    );

    if (!response.data?.elements) {
      console.log('No places found in response');
      return [];
    }

    const places = await Promise.all(
      response.data.elements
        .filter((element: any) => element.tags && element.tags.name)
        .map(async (element: any) => {
          const lat = element.lat || element.center?.lat;
          const lon = element.lon || element.center?.lon;
          const name = element.tags.name;

          console.log('Processing place:', { name, lat, lon });
          const placeDetails = await getPlaceDetails(name, lat, lon);
          
          return {
            id: element.id.toString(),
            name: name,
            type: element.tags.amenity || element.tags.leisure || 'venue',
            latitude: lat,
            longitude: lon,
            address: element.tags['addr:street'] ? 
              `${element.tags['addr:housenumber'] || ''} ${element.tags['addr:street']}, ${element.tags['addr:postcode'] || ''}`.trim() : 
              undefined,
            imageUrl: placeDetails?.imageUrl,
            reviews: placeDetails?.reviews || [],
            rating: placeDetails?.rating
          };
        })
    );

    return places.filter((place: Place) => place.latitude && place.longitude);
  } catch (error) {
    console.error('Error fetching places:', error);
    throw new Error('Failed to fetch nearby places in London');
  }
};

async function getPlaceDetails(name: string, lat: number, lon: number) {
  try {
    const searchUrl = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${lat},${lon}&radius=100&name=${encodeURIComponent(name)}&key=${GOOGLE_PLACES_API_KEY}`;
    console.log('Searching for place:', { name, lat, lon });
    
    const searchResponse = await axios.get(searchUrl);
    if (!searchResponse.data.results?.[0]) {
      console.log('No place found for:', name);
      return null;
    }

    const placeId = searchResponse.data.results[0].place_id;
    const detailsUrl = `https://maps.googleapis.com/maps/api/place/details/json?place_id=${placeId}&fields=rating,reviews,photos&key=${GOOGLE_PLACES_API_KEY}`;
    
    const detailsResponse = await axios.get(detailsUrl);
    const details = detailsResponse.data.result;

    if (!details) {
      console.log('No details found for place:', placeId);
      return null;
    }

    console.log('Found place details:', { 
      name, 
      hasPhotos: !!details.photos?.length,
      hasReviews: !!details.reviews?.length 
    });

    const photoReference = details.photos?.[0]?.photo_reference;
    return {
      rating: details.rating,
      reviews: details.reviews?.map((review: any) => ({
        rating: review.rating,
        text: review.text,
        date: review.time
      })) || [],
      imageUrl: photoReference ? 
        `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${photoReference}&key=${GOOGLE_PLACES_API_KEY}` :
        undefined
    };
  } catch (error: any) {
    console.error('Error fetching place details:', error.response?.data || error.message);
    return null;
  }
}
