import axios from 'axios';

export interface Review {
  rating: number;
  text: string;
  date: string;
}

export interface Place {
  id: string;
  name: string;
  type: string;
  latitude: number;
  longitude: number;
  address?: string;
  distanceToLoc1?: number;
  distanceToLoc2?: number;
  imageUrl?: string;
  reviews?: Review[];
  rating?: number;
}

const GOOGLE_PLACES_API_KEY = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;

export const searchNearbyPlaces = async (latitude: number, longitude: number, radius: number = 6400): Promise<Place[]> => {
  try {
    console.log('Searching for places near:', { latitude, longitude });
    
    const response = await axios.get('/maps/api/place/nearbysearch/json', {
      params: {
        location: `${latitude},${longitude}`,
        radius: radius,
        type: ['restaurant', 'cafe', 'bar', 'park', 'movie_theater'].join('|'),
        key: GOOGLE_PLACES_API_KEY,
        rankby: 'prominence',
        sensor: false
      }
    });

    if (!response.data?.results) {
      console.log('No places found in response:', response.data);
      return [];
    }

    console.log('Found places:', response.data.results.length);

    const places = response.data.results
      .filter((place: any) => place.geometry && place.name)
      .map((place: any) => ({
        id: place.place_id,
        name: place.name,
        type: place.types?.[0] || 'venue',
        latitude: place.geometry.location.lat,
        longitude: place.geometry.location.lng,
        address: place.vicinity,
        rating: place.rating,
        imageUrl: place.photos?.[0] ? 
          `/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=${GOOGLE_PLACES_API_KEY}` :
          undefined
      }));

    return places;
  } catch (error) {
    console.error('Error fetching places:', error);
    if (error.response?.data?.error_message) {
      console.error('API Error:', error.response.data.error_message);
    }
    throw new Error('Failed to fetch nearby places. Please try again.');
  }
};
