import axios from 'axios';

export interface Place {
  id: string;
  name: string;
  type: string;
  latitude: number;
  longitude: number;
  address?: string;
  distanceToLoc1?: number;
  distanceToLoc2?: number;
}

export const searchNearbyPlaces = async (latitude: number, longitude: number, radius: number = 6400): Promise<Place[]> => {
  try {
    const viewboxSize = 0.1; // Roughly 11km at most latitudes
    const response = await axios.get(
      `https://nominatim.openstreetmap.org/search?` +
      `format=json&` +
      `amenity=restaurant,cafe,bar,pub,park,cinema,theatre&` +
      `bounded=1&` +
      `limit=50&` +
      `viewbox=${longitude - viewboxSize},${latitude + viewboxSize},${longitude + viewboxSize},${latitude - viewboxSize}&` +
      `lat=${latitude}&` +
      `lon=${longitude}`,
      {
        headers: {
          'User-Agent': 'EquidistantApp/1.0'
        }
      }
    );

    if (!response.data || response.data.length === 0) {
      return [];
    }

    return response.data.map((place: any) => ({
      id: place.place_id.toString(),
      name: place.display_name.split(',')[0],
      type: place.type || place.category || 'venue',
      latitude: parseFloat(place.lat),
      longitude: parseFloat(place.lon),
      address: place.display_name
    }));
  } catch (error) {
    console.error('Error fetching places:', error);
    throw new Error('Failed to fetch nearby places');
  }
};
